INSERT INTO `charities` VALUES ('60', 'Smile Train Philippines', 'Smile Train is an international children’s charity with a sustainable approach to a single, solvable problem: cleft lip and palate. Millions of children in developing countries with unrepaired clefts live in shame, but more importantly, have difficulty eating, breathing and speaking. Our sustainable model provides training and funding to empower local doctors in 85+ developing countries to provide 100% - free cleft services in their communities.', '64', '2018-04-28 14:57:03', '10', null, null, '<p style=\"text-align:justify\">Smile Train is an international children&rsquo;s charity with a sustainable approach to a single, solvable problem: cleft lip and palate. Millions of children in developing countries with unrepaired clefts live in shame, but more importantly, have difficulty eating, breathing and speaking. Our sustainable model provides training and funding to empower local doctors in 85+ developing countries to provide 100% - free cleft services in their communities.</p>\r\n', '1', 'charities', 'None');
INSERT INTO `charities` VALUES ('61', 'He Cares Mission', 'He Cares Mission is a Christian non-profit organization that helps extremely poor street children in Metro Manila, Philippines by providing basic needs like food, clothing, education and medical assistance, as well as spiritual support. It was founded by full-time lay missionaries, Joe Dean Sola & his wife, Ardis in 1996.', '63', '2018-04-28 14:57:30', '10', null, null, '<p style=\"text-align:justify\">He Cares Mission is a Christian non-profit organization that helps extremely poor street children in Metro Manila, Philippines by providing basic needs like food, clothing, education and medical assistance, as well as spiritual support. It was founded by full-time lay missionaries, Joe Dean Sola &amp; his wife, Ardis in 1996.</p>\r\n', '1', 'charities', 'None');
INSERT INTO `charities_old` VALUES ('6', ' He Cares Mission', '<p style=\"text-align:center\"><img src=\"https://static.wixstatic.com/media/7ee9b6_6e0e94a41e364486a720f21fb6756d4a~mv2_d_3934_2678_s_4_2.jpg/v1/fill/w_330,h_200,al_c,q_80,usm_0.66_1.00_0.01/7ee9b6_6e0e94a41e364486a720f21fb6756d4a~mv2_d_3934_2678_s_4_2.webp\" /></p>\n\n<p style=\"text-align:justify\">He Cares Mission is a Christian non-profit organization that helps extremely poor street children in Metro Manila, Philippines by providing basic needs like food, clothing, education and medical assistance, as well as spiritual support. It was founded by full-time lay missionaries, Joe Dean Sola &amp; his wife, Ardis in 1996.</p>\n\n<hr />\n<p><a href=\"http://hecaresmission.com/\" target=\"_blank\">Visit Site</a></p>\n', '2018-02-06 16:45:42', '17', '2018-04-28 09:51:10', '10', '1');
INSERT INTO `charities_old` VALUES ('7', 'Smile Train Philippines', '<p style=\"text-align:center\"><img src=\"https://static.wixstatic.com/media/7ee9b6_71780916cc6642388dbee9596bde4a41~mv2.jpg/v1/fill/w_330,h_200,al_c,q_80,usm_0.66_1.00_0.01/7ee9b6_71780916cc6642388dbee9596bde4a41~mv2.webp\" /></p>\n\n<p style=\"text-align:justify\">Smile Train is an international children&rsquo;s charity with a sustainable approach to a single, solvable problem: cleft lip and palate. Millions of children in developing countries with unrepaired clefts live in shame, but more importantly, have difficulty eating, breathing and speaking. Our sustainable model provides training and funding to empower local doctors in 85+ developing countries to provide 100% - free cleft services in their communities.</p>\n\n<hr />\n<p><a href=\"https://www.smiletrain.ph/\">Visit Site</a></p>\n', '2018-02-06 16:48:45', '17', '2018-04-28 09:52:14', '10', '1');
