INSERT INTO `invitations` VALUES ('68', 'asdasdasdasd', 'asdasd', '67', '2018-05-01 14:39:03', '10', null, null, '<table>\r\n	<caption>LeBron James</caption>\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\"><a href=\"https://en.wikipedia.org/wiki/File:LeBron_James_(31944491583).jpg\"><img alt=\"LeBron James (31944491583).jpg\" src=\"https://upload.wikimedia.org/wikipedia/commons/thumb/4/4e/LeBron_James_%2831944491583%29.jpg/220px-LeBron_James_%2831944491583%29.jpg\" style=\"height:343px; width:220px\" /></a>\r\n			<p>James with the Cavaliers in 2017</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"2\">No. 23 &ndash; Cleveland Cavaliers</th>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Position</th>\r\n			<td><a href=\"https://en.wikipedia.org/wiki/Small_forward\" title=\"Small forward\">Small forward</a></td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">League</th>\r\n			<td><a href=\"https://en.wikipedia.org/wiki/National_Basketball_Association\" title=\"National Basketball Association\">NBA</a></td>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"2\">Personal information</th>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Born</th>\r\n			<td>December 30, 1984&nbsp;(age&nbsp;33)<br />\r\n			<a href=\"https://en.wikipedia.org/wiki/Akron,_Ohio\" title=\"Akron, Ohio\">Akron, Ohio</a></td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Nationality</th>\r\n			<td>American</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Listed height</th>\r\n			<td>6&nbsp;ft 8&nbsp;in (2.03&nbsp;m)</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Listed weight</th>\r\n			<td>250&nbsp;lb (113&nbsp;kg)</td>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"2\">Career information</th>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">High school</th>\r\n			<td><a href=\"https://en.wikipedia.org/wiki/St._Vincent%E2%80%93St._Mary_High_School\" title=\"St. Vincent–St. Mary High School\">St. Vincent&ndash;St. Mary</a><br />\r\n			(Akron, Ohio)</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\"><a href=\"https://