INSERT INTO `logs` VALUES ('379', 'Updated Career - ID:7, Career: PRODUCTION PLANNER for VALENZUELA', '17', '2018-02-06 17:12:39', 'careers', '\"{\\\"job_title\\\":\\\"PRODUCTION PLANNER for VALENZUELA\\\",\\\"job_description\\\":\\\"<p><strong>Minimum Qualifications:<\\\\\\/strong><\\\\\\/p>\\\\n\\\\n<ul>\\\\n\\\\t<li>Candidate must possess at least a Bachelor&#39;s\\\\\\/College Degree , Engineering (Industrial) or equivalent.<\\\\\\/li>\\\\n\\\\t<li>&nbsp;At least 2 year(s) of working experience in the related field is required for this position.<\\\\\\/li>\\\\n\\\\t<li>Preferably 1-4 Yrs Experienced Employees specializing in Engineering - Industrial or equivalent.<\\\\\\/li>\\\\n\\\\t<li>Full-Time position(s) available.<\\\\\\/li>\\\\n<\\\\\\/ul>\\\\n\\\\n<p><strong>Job Description:<\\\\\\/strong><\\\\\\/p>\\\\n\\\\n<ul>\\\\n\\\\t<li>Coordinates with Production, Material Planning, Logistics and Quality departments toattain production goals.<\\\\\\/li>\\\\n\\\\t<li>Ensure daily production requirements are achieved to meet customer expectation.<\\\\\\/li>\\\\n\\\\t<li>Ensures smooth operations throughout production and distribution of finished goods<\\\\\\/li>\\\\n\\\\t<li>Analyzes changing product demand patterns<\\\\\\/li>\\\\n\\\\t<li>Monitors and maintains inventory levels of finished products<\\\\\\/li>\\\\n\\\\t<li>Coordinates with Materials Planner to expedite flow of materials and documents to meet production schedule and customer expectations<\\\\\\/li>\\\\n\\\\t<li>Analyzes and prepares documents needed for production such as Filling Data Form and Blending Data Form<\\\\\\/li>\\\\n\\\\t<li>Execute data analysis to support finished goods demand.&nbsp;<\\\\\\/li>\\\\n\\\\t<li>Creates and maintains production schedule and prioritizes job orders for production optimization.<\\\\\\/li>\\\\n\\\\t<li>Complete status reports for production progress, work in process, and raw material inventory.<\\\\\\/li>\\\\n\\\\t<li>Interacts with Filling Supervisor on a daily basis to resolve issues regarding manufacturing efficiencies or additional information as requested from Sales and Logistics departments.<\\\\\\/li>\\\\n\\\\t<li>Coordinates with Materials Planner with regards to materials availability and shipments dates.<\\\\\\/li>\\\\n\\\\t<li>Tracks customers committed delivery dates<\\\\\\/li>\\\\n\\\\t<li>Plan production to attain company metrics like customer delivery timeliness and completeness<\\\\\\/li>\\\\n\\\\t<li>Interface with sales and finished product forecast integrity<\\\\\\/li>\\\\n\\\\t<li>Analyzes Sales forecasts on reasonableness and accuracy.<\\\\\\/li>\\\\n\\\\t<li>Creates and releases Production Orders and other planning related activities in SAP.<\\\\\\/li>\\\\n<\\\\\\/ul>\\\\n\\\",\\\"date_modified\\\":\\\"2018-02-06 17:12:39 PM\\\",\\\"modified_by\\\":\\\"17\\\",\\\"id\\\":\\\"7\\\"}\"');
