INSERT INTO `logs` VALUES ('489', 'Created Loyalty Page Contents - ID:57, Loyalty Page Contents Title: BENEFITS OF THE CARD', '10', '2018-02-10 10:52:58', 'loyalty_contents', '\"{\\\"title\\\":\\\"BENEFITS OF THE CARD\\\",\\\"description\\\":\\\"BENEFITS OF THE CARD\\\",\\\"content\\\":\\\"<ul>\\\\r\\\\n\\\\t<li>P200 = 1 Point<\\\\\\/li>\\\\r\\\\n\\\\t<li>1 Point = P1<\\\\\\/li>\\\\r\\\\n\\\\t<li>Birthday Month Bonus Points: Earn 2x points every time you fuel up during your birthday month.<\\\\\\/li>\\\\r\\\\n\\\\t<li>Frequent Visit Bonus Points: Earn additional 25 points when you accumulate at least P5,000 monthly fuel purchase.<\\\\\\/li>\\\\r\\\\n<\\\\\\/ul>\\\\r\\\\n\\\",\\\"date_created\\\":\\\"2018-02-10 10:52:57 AM\\\",\\\"status\\\":\\\"1\\\",\\\"created_by\\\":\\\"10\\\",\\\"content_type\\\":\\\"loyalty_contents\\\",\\\"id\\\":57}\"');
