INSERT INTO `logs` VALUES ('640', 'Updated Career - ID:7, Career: Smile Train Philippines', '10', '2018-04-28 09:52:14', 'charities', '\"{\\\"title\\\":\\\"Smile Train Philippines\\\",\\\"description\\\":\\\"<p style=\\\\\\\"text-align:center\\\\\\\"><img src=\\\\\\\"https:\\\\\\/\\\\\\/static.wixstatic.com\\\\\\/media\\\\\\/7ee9b6_71780916cc6642388dbee9596bde4a41~mv2.jpg\\\\\\/v1\\\\\\/fill\\\\\\/w_330,h_200,al_c,q_80,usm_0.66_1.00_0.01\\\\\\/7ee9b6_71780916cc6642388dbee9596bde4a41~mv2.webp\\\\\\\" \\\\\\/><\\\\\\/p>\\\\n\\\\n<p style=\\\\\\\"text-align:justify\\\\\\\">Smile Train is an international children&rsquo;s charity with a sustainable approach to a single, solvable problem: cleft lip and palate. Millions of children in developing countries with unrepaired clefts live in shame, but more importantly, have difficulty eating, breathing and speaking. Our sustainable model provides training and funding to empower local doctors in 85+ developing countries to provide 100% - free cleft services in their communities.<\\\\\\/p>\\\\n\\\\n<hr \\\\\\/>\\\\n<p><a href=\\\\\\\"https:\\\\\\/\\\\\\/www.smiletrain.ph\\\\\\/\\\\\\\">Visit Site<\\\\\\/a><\\\\\\/p>\\\\n\\\",\\\"status\\\":\\\"1\\\",\\\"date_modified\\\":\\\"2018-04-28 09:52:14 AM\\\",\\\"modified_by\\\":\\\"10\\\",\\\"id\\\":\\\"7\\\"}\"');
INSERT INTO `logs` VALUES ('641', 'Deleted Products - ID:, Products Category: BILLING ASSOCIATE for ORTIGAS', '10', '2018-04-28 09:52:18', 'charities', '\"{\\\"id\\\":\\\"8\\\",\\\"title\\\":\\\"BILLING ASSOCIATE for ORTIGAS\\\",\\\"description\\\":\\\"<p><strong>Objective: To ensure timely&nbsp;&nbsp;dispatch of deliveries and ensure accurate processing of due<br \\\\\\/>\\\\nlist from the system.<\\\\\\/strong><\\\\\\/p>\\\\n\\\\n<p><br \\\\\\/>\\\\n<strong>Minimum Qualifications and Job Requirements:<\\\\\\/strong><\\\\\\/p>\\\\n\\\\n<ul>\\\\n\\\\t<li>Candidate must possess at least a Bachelor&#39;s\\\\\\/College Degree , any field.<\\\\\\/li>\\\\n\\\\t<li>At least 2 to 3 year(s) of working experience in Billing or Order Processing.<\\\\\\/li>\\\\n\\\\t<li>Has SAP experience is an advantage.<\\\\\\/li>\\\\n\\\\t<li>Very keen with details<\\\\\\/li>\\\\n\\\\t<li>With background in doing&nbsp;<strong>INVENTORY<\\\\\\/strong>&nbsp;is an&nbsp;<strong>advantage<\\\\\\/strong>.<\\\\\\/li>\\\\n\\\\t<li><strong>Applicants must be willing to work in Ortigas<\\\\\\/strong>.<\\\\\\/li>\\\\n\\\\t<li>Willing to work in&nbsp;<strong>shifting schedule<\\\\\\/strong>.<\\\\\\/li>\\\\n\\\\t<li>Willing to work during&nbsp;<strong>Saturdays and holidays<\\\\\\/strong>.<\\\\\\/li>\\\\n<\\\\\\/ul>\\\\n\\\\n<p><strong>Job Description:<\\\\\\/strong><\\\\\\/p>\\\\n\\\\n<ul>\\\\n\\\\t<li>Ensure accurate picking and posting in SAP based on the Due list.<\\\\\\/li>\\\\n\\\\t<li>Ensure timely posting of transactions and invoice is printed within the day.<\\\\\\/li>\\\\n\\\\t<li>Perform regular billing change (daily) in SAP.<\\\\\\/li>\\\\n\\\\t<li>Advise Logistics for drivers who are non-compliant with regard the submission of delivery documents.<\\\\\\/li>\\\\n\\\\t<li>Ensure completeness of Delivery Receipt (DR) and Sales Invoice (SI) series.<\\\\\\/li>\\\\n\\\\t<li>Communicate to Customer Service or Sales Dept. if there are changes in delivery, returns and partial deliveries.<\\\\\\/li>\